import React, { useState } from "react";
import TodoForm from "./TodoForm";
import "./App.css";

const App = () => {
  const Todo = ({ todo, index }) => <div className="todo">{todo.text}</div>;
  const [todos, setTodos] = useState([
    {
      text: "Estudar ReactJS",
      isCompleted: false
    },
    {
      text: "Estudar HOOKS",
      isCompleted: false
    },
    {
      text: "Estudar Angular",
      isCompleted: false
    },
    {
      text: "Buscar Comida",
      isCompleted: true
    }
  ]);

  const addTodo = text => {
    const NewTodos = [...todos, { text }];
    setTodos(NewTodos);
  };

  return (
    <div className="app">
      <TodoForm addTodo={addTodo} />
      <div className="todo-list">
        {todos.map((todo, index) => (
          <Todo key={index} index={index} todo={todo} />
        ))}
      </div>
    </div>
  );
};

export default App;
